import React, { useState } from 'react';
import QueryForm from './components/QueryForm';
import TraceViewer from './components/TraceViewer';
import './App.css';

function App() {
  const [query, setQuery] = useState('');
  const [response, setResponse] = useState(null);
  const [traceLogs, setTraceLogs] = useState([]);

  const handleQuerySubmit = async () => {
    console.log("Sending query:", query); // üëà Add this to debug

    const trimmedQuery = query.trim();
    if (trimmedQuery === '') {
      setResponse({
        summary: "‚ùå No valid query provided.",
        trace: [{
          role: "System",
          action: "Validation",
          result: "Empty or invalid query."
        }]
      });
      return;
    }

    const res = await fetch('http://localhost:5000/api/ask', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ query: trimmedQuery }) // üëà trim before sending
    });

    const data = await res.json();
    setResponse(data.summary ? data : null);
    setTraceLogs(data.trace || []);
  };

  return (
    <div className="App">
      <h1>üåê Planner Agent</h1>
      <QueryForm query={query} setQuery={setQuery} onSubmit={handleQuerySubmit} />
      {response && (
        <div className="response-box">
          <h2>Final Response</h2>
          <pre>{JSON.stringify(response, null, 2)}</pre>
        </div>
      )}
      <TraceViewer logs={traceLogs} />
    </div>
  );
}

export default App;
